package com.jzxl.thecustomview.widget;import android.content.Context;import android.content.res.TypedArray;import android.support.annotation.Nullable;import android.util.AttributeSet;import android.view.View;import com.jzxl.thecustomview.R;/** * @author wangyongyong * @date * @Description */public class PageIndicatorView extends View {    private int count;    public PageIndicatorView(Context context) {        this(context, null);    }    public PageIndicatorView(Context context, @Nullable AttributeSet attrs) {        this(context, attrs, 0);    }    public PageIndicatorView(Context context, @Nullable AttributeSet attrs, int defStyleAttr) {        super(context, attrs, defStyleAttr);        TypedArray typedArray = context.obtainStyledAttributes(attrs, R.styleable.PageIndicatorView);        count = typedArray.getInt(R.styleable.PageIndicatorView_piv_count, 0);        typedArray.recycle();    }    @Override    protected void onMeasure(int widthMeasureSpec, int heightMeasureSpec) {        super.onMeasure(widthMeasureSpec, heightMeasureSpec);    }}